    @Override
    protected void formStructure(PatternMatchContext context) {
        super.formStructure(context);
        initializeAbilities();
        Object type = context.get("ElevatorMotorTier");
        if (type instanceof IElevatorMotorTier) {
            this.motorTier = ((IElevatorMotorTier) type).getTier() + 1;
        } else
            this.motorTier = 0;
    }

    @Override
    public void checkStructurePattern() {
        if(this.getOffsetTimer() % 20 == 0 || this.isFirstTick()) {
            if (!this.isStructureFormed()) {
                reinitializeStructurePattern();
            }
            super.checkStructurePattern();
        }
    }

    @Override
    public void invalidateStructure() {
        super.invalidateStructure();
        resetTileAbilities();
        this.spaceElevatorReceivers.forEach(ISpaceElevatorReceiver::sentWorkingDisabled);
        this.spaceElevatorReceivers.clear();
    }

    protected void initializeAbilities() {
        List<IEnergyContainer> powerInput = new ArrayList<>(getAbilities(MultiblockAbility.INPUT_ENERGY));
        powerInput.addAll(getAbilities(MultiblockAbility.INPUT_LASER));
        this.energyContainer = new EnergyContainerList(powerInput);
    }

    private void resetTileAbilities() {
        this.energyContainer = new EnergyContainerList(new ArrayList<>());
    }

    @NotNull
    protected TraceabilityPredicate modulePredicate() {
        return new TraceabilityPredicate(blockWorldState -> {
            IBlockState blockState = blockWorldState.getBlockState();
            Block block = blockState.getBlock();
            if(block instanceof ElevatorCasing) {
                ElevatorCasing.CasingType casingType = ((ElevatorCasing) blockState.getBlock()).getState(blockState);
                if(casingType == ElevatorCasing.CasingType.ELEVATOR_BASE_CASING) return true;
            }

            TileEntity tileEntity = blockWorldState.getTileEntity();
            if(!(tileEntity instanceof IGregTechTileEntity)) return false;

            MetaTileEntity metaTileEntity = ((IGregTechTileEntity) tileEntity).getMetaTileEntity();

            if(metaTileEntity instanceof ISpaceElevatorProvider)
                return false;

            if(!(metaTileEntity instanceof ISpaceElevatorReceiver)) return false;

            ISpaceElevatorReceiver spaceElevatorReceiver = (ISpaceElevatorReceiver) metaTileEntity;
            if(spaceElevatorReceiver.getSpaceElevator() != this) {
                spaceElevatorReceiver.setSpaceElevator(this);
                this.spaceElevatorReceivers.add(spaceElevatorReceiver);
            }
            return true;
        });
    }

    @Override
    protected ModularUI createUI(EntityPlayer entityPlayer) {
        return createUITemplate(entityPlayer).build(getHolder(), entityPlayer);
    }

    protected gregtech.api.gui.ModularUI.Builder createUITemplate(EntityPlayer entityPlayer) {
        gregtech.api.gui.ModularUI.Builder builder = gregtech.api.gui.ModularUI.builder(GuiTextures.BACKGROUND, 240, 208);

        // Display
        builder.image(4, 4, 232, 109, GuiTextures.DISPLAY);


        builder.label(9, 9, getMetaFullName(), 0xFFFFFF);

        builder.widget(new AdvancedTextWidget(9, 20, this::addDisplayText, 0xFFFFFF)
                .setMaxWidthLimit(220)
                .setClickHandler(this::handleDisplayClick));

        // Power Button
        builder.widget(new ImageCycleButtonWidget(215, 183, 18, 18, ClientHandler.BUTTON_ELEVATOR_EXTENSION,
                this::isExtended, this::setExtended).setTooltipHoverString("gcyl.gui.multiblock.space_elevator_extended"));
        builder.widget(new ImageWidget(215, 201, 18, 6, GuiTextures.BUTTON_POWER_DETAIL));

        // Voiding Mode Button
        builder.widget(new ImageWidget(215, 161, 18, 18, GuiTextures.BUTTON_VOID_NONE)
                .setTooltip("gregtech.gui.multiblock_voiding_not_supported"));

        builder.widget(new ImageWidget(215, 143, 18, 18, GuiTextures.BUTTON_NO_DISTINCT_BUSES)
                .setTooltip("gregtech.multiblock.universal.distinct_not_supported"));

        // Flex Button
        builder.widget(getFlexButton(215, 125, 18, 18));

        builder.bindPlayerInventory(entityPlayer.inventory, 125);
        return builder;
    }

    private boolean isExtended() {
        return this.isExtended;
    }

    private void setExtended(boolean bool) {
        this.isExtended = bool;
        reinitializeStructurePattern();
    }

    @Override
    public NBTTagCompound writeToNBT(NBTTagCompound data) {
        data.setBoolean("extended", this.isExtended);
        return super.writeToNBT(data);
    }

    @Override
    public void readFromNBT(NBTTagCompound data) {
        super.readFromNBT(data);
        this.isExtended = data.getBoolean("extended");
    }

    @Override
    public void writeInitialSyncData(PacketBuffer buf) {
        super.writeInitialSyncData(buf);
        buf.writeBoolean(this.isExtended);
    }

    @Override
    public void receiveInitialSyncData(PacketBuffer buf) {
        super.receiveInitialSyncData(buf);
        this.isExtended = buf.readBoolean();
    }

    @Override
    public int getMotorTier() {
        return this.motorTier;
    }

    @Override
    public IEnergyContainer getEnergyContainerForModules() {
        return this.energyContainer;
    }
